package learnjob;

public class Char {

	public static void main(String[] args) {
		int row=4;
		int col=1;
		
		
		for(int i=1;i<=row;i++) {
			char c='A';
			for(int j=1;j<=col;j++) {
				System.out.print(c+"");
				c++;
			}
			System.out.println();
			col++;
				
			
		}
		
		
		
		
		
		// TODO Auto-generated method stub

	}

}
